CREATE TABLE VEICOLI(TARGA VARCHAR(255), MARCA VARCHAR(255), MODELLO VARCHAR(255), TIPOLOGIA VARCHAR(255), DESCRIZIONE VARCHAR(255), DATA_ACQUISTO DATE, PREZZO_ACQUISTO VARCHAR(255), PREZZO_VENDITA VARCHAR(255));
CREATE TABLE MANUTENZIONI(TARGA VARCHAR(255), DATA_INIZIO DATE, DATA_FINE DATE, DESCRIZIONE VARCHAR(255), COSTO VARCHAR(255), OFFICINA VARCHAR(255));
CREATE TABLE PRIVATI(EMAIL VARCHAR(255), NOME VARCHAR(255), COGNOME VARCHAR(255), DATA_NASCITA VARCHAR(255), CODICE_FISCALE VARCHAR(255)); 
CREATE TABLE AZIENDE(EMAIL VARCHAR(255), RAGIONE_SOCIALE VARCHAR(255), PARTITA_IVA VARCHAR(255)); 
CREATE TABLE TARIFFE(ID_TARIFFA VARCHAR(255), MODELLO_VEICOLO VARCHAR(255), UNITA_DI_TEMPO VARCHAR(255), NUMERO_UNITA VARCHAR(255), COSTO VARCHAR(255), PERCENTUALE_CREDITO VARCHAR(255));
CREATE TABLE FEDELTA(LIVELLO VARCHAR(255), SPESA_GIORNALIERA VARCHAR(255), CASHBACK VARCHAR(255)); 
CREATE TABLE NOLEGGI(EMAIL_CLIENTE VARCHAR(255), TARGA VARCHAR(255), DATA_INIZIO VARCHAR(255), DATA_FINE_PREVISTA VARCHAR(255), DATA_FINE_EFFETTIVA VARCHAR(255), PATENTE VARCHAR(255), DURATA VARCHAR(255), ID_TARIFFA VARCHAR(255));
